document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("expand-all"),t=document.getElementById("collapse-all");e&&e.addEventListener("click",()=>{document.querySelectorAll(".tree-list details").forEach(e=>{e.open=!0})}),t&&t.addEventListener("click",()=>{document.querySelectorAll(".tree-list details").forEach(e=>{e.open=!1})});const n=1;document.querySelectorAll(".tree-list details").forEach(e=>{const t=parseInt(e.querySelector("summary")?.dataset.level||"0");t<=n&&(e.open=!0)})})